    },
    shadowOpacity: 0.20,
    shadowRadius: 1.41,
    elevation: 2,
    elevation: 5,
  },
  eventImage: {
    width: 220,
    height: 123.75,
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
  },
  eventText: {
    margin: 10
  },
  eventDate: {
    margin: 10,
    color: '#c9093d',
  },
  circularImage: {
    borderRadius: 400 / 2,
    width: 50,
    height: 50,
    marginStart: 5,
  },
  socialContainer: {
    paddingTop: 15
  },
  buttonMail: {
    backgroundColor: 'white',
    flexDirection: 'column',
    padding: 15,
    justifyContent: "flex-start",
    alignItems: "flex-start",
    width: '90%',
    borderRadius: 10,
    marginTop: 7.5,
    marginBottom: 7.5,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.20,
    shadowRadius: 1.41,
    elevation: 2,
