    paddingTop: 20,
    paddingBottom: 20,
    paddingStart: 20,
    paddingEnd: 20,
    backgroundColor: 'white',
    marginBottom: 30,
    borderRadius: 10,
  },
  checkMailButton: {
    backgroundColor: '#c9093d',
    paddingTop: 12,
    paddingBottom: 12,
    paddingStart: 25,
    paddingEnd: 25,
    borderRadius: 400 / 2,
    marginTop: 10
  },
  modalContent: {
    backgroundColor: "#c9093d",
    padding: 0,
    width: "100%",
    height: "100%",
    borderRadius: 4,
    borderColor: "rgba(0, 0, 0, 0.1)",
    flexDirection: "column",
  },
  bottomModal: {
    justifyContent: "center",
    margin: 0,
  },
  topIconsContainer: {
    flexDirection: "row",
    paddingTop: 50,
    marginBottom: 20,
  },
  logoIcon: {
    width: 200,
    resizeMode: "contain",
